import { useState, useEffect } from "react";
import { Frame, Table, Button } from "./components";

/* App Configuration */
const CONFIG = {
  url: "http://universities.hipolabs.com/search?country=Australia",
};

function App() {
  /* Set state for API data */
  const [data, setData] = useState();

  /* Fetch data from API */
  const fetchDataJSON = async () => {
    try {
      const response = await fetch(CONFIG["url"]);
      const rawData = await response.json();
      setData(rawData);
    } catch (error) {
      console.error(error);
      setData("API is not available");
    }
  };

  /* Load button handler */
  const loadButtonHandler = async () => {
    await fetchDataJSON();
  };

  /* Delete button handler */
  const deleteButtonHandler = () => {
    setData();
  };

  return (
    <Frame>
      <div className="grid grid-rows-1 grid-flow-col">
        <div>
          <Button onClick={loadButtonHandler}><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
  <path stroke-linecap="round" stroke-linejoin="round" d="M19.5 12c0-1.232-.046-2.453-.138-3.662a4.006 4.006 0 00-3.7-3.7 48.678 48.678 0 00-7.324 0 4.006 4.006 0 00-3.7 3.7c-.017.22-.032.441-.046.662M19.5 12l3-3m-3 3l-3-3m-12 3c0 1.232.046 2.453.138 3.662a4.006 4.006 0 003.7 3.7 48.656 48.656 0 007.324 0 4.006 4.006 0 003.7-3.7c.017-.22.032-.441.046-.662M4.5 12l3 3m-3-3l-3 3" />
</svg>Load</Button>
        </div>
        <div>
          <Button buttonStyle="add">Add</Button>
          <Button buttonStyle="delete" onClick={deleteButtonHandler}>
            Delete
          </Button>
        </div>
      </div>
      <Table data={data} />
    </Frame>
  );
}

export default App;
