import { useState, useEffect } from "react";
import { Frame, Table, Button } from "./components";

/* App Configuration */
const CONFIG = {
  url: "http://universities.hipolabs.com/search?country=Australia",
};

function App() {
  /* Set state for API data */
  const [data, setData] = useState();

  /* Fetch data from API */
  const fetchDataJSON = async () => {
    try {
      const response = await fetch(CONFIG["url"]);
      const responseData = await response.json();
      setData(responseData);
    } catch (error) {
      console.error(error);
      setData("API is not available");
    }
  };

  /* Load button handler */
  const loadButtonHandler = async () => {
    await fetchDataJSON();
  };
  /* Add button handler */
  const addButtonHandler = () => {
    if (!data) {
      alert(1);
      return 1;
    }
    try {
      setData([...data, data[0]]);
    } catch (error) {
      console.error(error);
    }
  };
  /* Delete button handler */
  const deleteButtonHandler = () => {
    try {
      const trimLastRow = data.slice(0, data.length - 1);
      setData(trimLastRow);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Frame>
      <div className="grid grid-rows-1 grid-flow-col">
        <div>
          <Button icon="loading" onClick={loadButtonHandler}>
            Load
          </Button>
        </div>
        <div>
          <Button buttonStyle="add" icon="adding" onClick={addButtonHandler}>
            Add
          </Button>
          <Button
            buttonStyle="delete"
            icon="delete"
            onClick={deleteButtonHandler}
          >
            Delete
          </Button>
        </div>
      </div>
      <Table dataLists={data} />
    </Frame>
  );
}

export default App;
