const Button = ({ children, buttonStyle = "default", ...props }) => {
  const style = {
    default: "bg-gray-500 text-slate-50 hover:bg-gray-200 hover:text-gray-500 focus:ring-indigo-500 rounded-md",
    add: "bg-green-500 text-slate-50 hover:bg-green-200 hover:text-green-500 focus:ring-indigo-500",
    delete: "bg-red-500 text-slate-50 hover:bg-red-200 hover:text-red-500 focus:ring-indigo-500",
  };
  return (
      <button
        className={`inline-flex items-center justify-center p-2  focus:outline-none focus:ring-2 focus:ring-inset ${style[buttonStyle]}`}
        {...props}
      >
        {children}
      </button>
  );
};

export default Button;
