import { useState, useEffect } from "react";
import { Table, Button } from "./components";

/* App Configuration */
const CONFIG = {
  url: "http://universities.hipolabs.com/search?country=Australia",
};

function App() {
  /* Set state for API data */
  const [data, setData] = useState();

  /* Fetch data from API */
  const fetchDataJSON = async () => {
    try {
      const response = await fetch(CONFIG["url"]);
      const rawData = await response.json();
      setData(rawData);
    } catch (error) {
      console.error(error);
      setData("API is not available");
    }
  };

  /* Load button handler */
  const loadButtonHandler = async () => {
    await fetchDataJSON();
  };

    /* Delete button handler */
    const deleteButtonHandler = () => {
      setData("");
    };

  return (
    <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
      <div className="mx-auto max-w-3xl">
        <Table />
        <Button onClick={loadButtonHandler}>Load</Button>
        <Button buttonStyle="add">Add</Button>
        <Button buttonStyle="delete">Delete</Button>
        <div>{JSON.stringify(data)}</div>
      </div>
    </div>
  );
}

export default App;
