import { useState, useEffect } from "react";
import { Frame, Table, Button } from "./components";

/* App Configuration */
const CONFIG = {
  url: "http://universities.hipolabs.com/search?country=Australia",
};

function App() {
  /* Set state for API data */
  const [data, setData] = useState();

  /* Fetch data from API */
  const fetchDataJSON = async () => {
    try {
      const response = await fetch(CONFIG["url"]);
      const rawData = await response.json();
      setData(rawData);
    } catch (error) {
      console.error(error);
      setData("API is not available");
    }
  };

  /* Load button handler */
  const loadButtonHandler = async () => {
    await fetchDataJSON();
  };

  /* Delete button handler */
  const deleteButtonHandler = () => {
    setData();
  };

  return (
    <Frame>
      <div className="grid grid-rows-1 grid-flow-col gap-4">
        <div>
          <Button onClick={loadButtonHandler}>Load</Button>
        </div>
        <div>
          {" "}
          <Button buttonStyle="add">Add</Button>
        </div>
        <div>
          {" "}
          <Button buttonStyle="delete" onClick={deleteButtonHandler}>
            Delete
          </Button>
        </div>
      </div>
      <Table />
      <div>{JSON.stringify(data)}</div>
    </Frame>
  );
}

export default App;
